<?php/** * CakeGallery Helper * * * @category Helper * @package  CakeGallery * @version  1.0 * @author   Vitor Pacheco Costa <vitor-p.c@hotmail.com> * @license  http://www.opensource.org/licenses/mit-license.php The MIT License */class CakeGalleryHelper extends AppHelper {	public function afterRender() {		if (ClassRegistry::getObject('view')) {			echo $this->View->element('gallery_include_js', array('plugin' => 'cake_gallery'));		}	}	public function replaceForAlbum($subject){		preg_match('/\[Gallery:(.*)\]/', $subject[0], $matches);		return $this->View->element('gallery_album', array('plugin' => 'cake_gallery', 'slug' => $matches[1], 'cache' => array('key' => $matches[1], 'time' => '5 mins')));	}}